mapper.update:
UPDATE PRODUCT
PROD_NAME = #{prodName:VARCHAR} , PROD_DETAIL = #{prodDetail:VARCHAR}, MANUFACTURE_DAY = #{manuDate:VARCHAR} , PRICE = #{price:NUMERIC}, IMAGE_FILE = #{fileName:VARCHAR}
WHERE PROD_NO = #{prodNo}

mapper.select:
	  	SELECT *
	  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
	  					FROM		(	select p.*, t.TRAN_STATUS_CODE from PRODUCT p, transaction t WHERE p.prod_no=t.prod_no(+)
											
													
										 				
													
													
										 				and p.PROD_NAME Like '%${search.searchKeyword}%'
													
													
										 				
										 					and p.PRICE = #{singlePrice}
										 				
										 				
										 					and p.PRICE BETWEEN #{singlePrice} and #{doublePrice}
										 				
													
											
											
												
												order by p.PRICE DESC
												
												
												order by p.PRICE ASC
												
											
											
											ORDER BY p.prod_no
											
											 ) inner_table WHERE ROWNUM <= #{search.currentPage}*#{search.pageSize} )
  		WHERE row_seq BETWEEN (#{search.currentPage}-1)*#{search.pageSize}+1
  		AND #{search.currentPage}*#{search.pageSize}

  	SELECT COUNT(*)
  			FROM(		select p.*, t.TRAN_STATUS_CODE from PRODUCT p, transaction t WHERE p.prod_no=t.prod_no(+)
										
												
									 				
												
												
									 				and p.PROD_NAME Like '%${search.searchKeyword}%'
												
												
									 				
									 					and p.PRICE = #{singlePrice}
									 				
									 				
									 					and p.PRICE BETWEEN #{singlePrice} and #{doublePrice}
									 				
												
										
										
											
											order by p.PRICE DESC
											
											
											order by p.PRICE ASC
											
										
										
										ORDER BY p.prod_no
										
										) countTable
